import xml.etree.ElementTree as ET
from lxml import etree

# Function to load and process the data
def load_data(xml_data):
    # Add the code here that converts the XML data to a pandas DataFrame
    # For demonstration, I'm creating a sample DataFrame
    #root = ET.fromstring(xml_data)
    parser = etree.XMLParser(recover=True)

    root = etree.fromstring(xml_data, parser=parser)

    # Create a list to hold the parsed data
    data = []
    rows=0
    # Iterate over each 'ROW' element
    for row in root.findall('ROW'):
        state = row.find('STATES').text
        def safe_float_convert(text):
            try:
                return float(text)
            except ValueError:
                return None  # or you can choose a default value like 0.0

        men_smoke = safe_float_convert(row.find('PERSONAL_HABITS_AGE_15_YEARS_AND_ABOVE_percent_-_MEN_WHO_SMOKE').text)
        women_smoke = safe_float_convert(row.find('PERSONAL_HABITS_AGE_15_YEARS_AND_ABOVE_percent_-_WOMEN_WHO_SMOKE').text)
        men_alcohol = safe_float_convert(row.find('PERSONAL_HABITS_AGE_15_YEARS_AND_ABOVE_percent_-_MEN_WHO_CONSUME_ALCOHOL').text)
        women_alcohol = safe_float_convert(row.find('PERSONAL_HABITS_AGE_15_YEARS_AND_ABOVE_percent_-_WOMEN_WHO_CONSUME_ALCOHOL').text)
        rows+=1
        # Add the parsed data to the list
        data.append({
            'State': state,
            'Men_Smoke': men_smoke,
            'Women_Smoke': women_smoke,
            'Men_Alcohol': men_alcohol,
            'Women_Alcohol': women_alcohol
        })

    # Convert the list to a DataFrame
    df = pd.DataFrame(data)
    return df

