# Function to parse XML and return DataFrame
def parse_xml(xml_data):
    root = ET.fromstring(xml_data)
    data = []
    for row in root.findall('ROW'):
        indicators = row.find('INDICATORS').text
        data_row = {'INDICATORS': indicators}
        for element in row:
            if element.tag != 'INDICATORS':
                try:
                    data_row[element.tag] = float(element.text) if element.text != 'NA' else None
                except ValueError:
                    data_row[element.tag] = None
        data.append(data_row)
    return pd.DataFrame(data)

# Load the data
df = parse_xml(xml_data)

# Streamlit application
def main():
    st.title("Health Data Dashboard")

    # Selection for indicator
    indicator = st.selectbox("Select an Indicator", df['INDICATORS'].unique())

    # Filter data based on the indicator
    indicator_data = df[df['INDICATORS'] == indicator].drop('INDICATORS', axis=1).T
    indicator_data.columns = ['Value']
    indicator_data = indicator_data.dropna()

    # Display the data
    st.write("Data for Selected Indicator:")
    st.write(indicator_data)

    # Plotting
    st.write(f"Visualization for {indicator}")
    fig, ax = plt.subplots(figsize=(10, 6))
    indicator_data.plot(kind='bar', ax=ax)
    plt.xticks(rotation=45, ha="right")
    plt.tight_layout()
    st.pyplot(fig)

if __name__ == "__main__":
    main()

